const lead = $input.all()[0];

if (!lead.solar || !lead.solar.estimatedCapacityKw) {
  return [{
    json: {
      ...lead,
      subsidy: {
        eligible: false,
        reason: 'No solar capacity data available'
      }
    }
  }];
}

const SubsidyCalculator = require('/Users/avion/Documents.nosync/projects/vorzimmerdrache/integrations/enrichment/subsidy-calculator.js');
const calculator = new SubsidyCalculator();

const projectCost = lead.system?.estimatedCostEUR || 0;
const roofSizeKWp = lead.solar.estimatedCapacityKw;
const systemType = 'pv';
const includeBAFA = false;

const subsidyResult = calculator.calculateCombinedSubsidies(
  projectCost,
  roofSizeKWp,
  systemType,
  includeBAFA
);

const enrichedLead = {
  ...lead,
  subsidy: {
    kfw270: subsidyResult.kfw,
    bafa: subsidyResult.bafa,
    totalBenefit: subsidyResult.totalBenefit,
    totalSubsidy: subsidyResult.totalSubsidy,
    combinedValue: subsidyResult.combinedValue,
    eligible: subsidyResult.kfw.eligible || subsidyResult.bafa.eligible,
    notes: subsidyResult.notes
  },
  enrichedAt: new Date().toISOString()
};

return [{ json: enrichedLead }];

const lead = $input.first().json;
const subsidy = lead.subsidy;

const notes = [];

if (subsidy.kfw270.eligible) {
  const kfw = subsidy.kfw270;
  notes.push(`KfW 270: bis €${kfw.maxLoan.toLocaleString('de-DE')} verfügbar bei ${kfw.interestRatePercent}% Zinsen`);
}

if (subsidy.bafa.eligible) {
  notes.push(`BAFA Zuschuss: bis €${subsidy.bafa.subsidy.toLocaleString('de-DE')}`);
}

if (subsidy.combinedValue > 0) {
  notes.push(`Gesamtvorteil: bis €${subsidy.combinedValue.toLocaleString('de-DE')}`);
}

return [{
  json: {
    ...lead,
    subsidyNotes: notes.join('\n'),
    subsidySummary: {
      kfwEligible: subsidy.kfw270.eligible,
      bafaEligible: subsidy.bafa.eligible,
      kfwAmount: subsidy.kfw270.maxLoan,
      bafaAmount: subsidy.bafa.subsidy,
      total: subsidy.combinedValue
    }
  }
}];

const lead = $input.first().json;
const fs = require('fs');
const templatePath = '/Users/avion/Documents.nosync/projects/vorzimmerdrache/config/subsidy-message-template.txt';

let template = 'Guten Tag {{name}},';
try {
  template = fs.readFileSync(templatePath, 'utf8');
} catch (error) {
  console.log('Using default template');
}

const kwp = lead.solar?.estimatedCapacityKw || 0;
const kfwAmount = lead.subsidy?.kfw270?.maxLoan || 0;
const bafaEligible = lead.subsidy?.bafa?.eligible || false;
const bafaAmount = lead.subsidy?.bafa?.subsidy || 0;

let message = template
  .replace(/{{name}}/g, lead.name || 'Kunde')
  .replace(/{{kwp}}/g, kwp.toFixed(1))
  .replace(/{{kfw_amount}}/g, kfwAmount.toLocaleString('de-DE'))
  .replace(/{{bafa_amount}}/g, bafaAmount.toLocaleString('de-DE'));

if (!bafaEligible) {
  message = message.replace(/{{#if bafa_eligible}}[\s\S]*?{{\/if}}/g, '');
} else {
  message = message.replace(/{{#if bafa_eligible}}/g, '').replace(/{{\/if}}/g, '');
}

return [{
  json: {
    ...lead,
    whatsappMessage: message.trim()
  }
}];

